import { React, useState } from "react";
import "./BMICalculator.css";

function BMICalculator() {
  const [bmi, setBmi] = useState(undefined);
  const [weight, setWeight] = useState();

  function bmiCalculate(e) {
    e.preventDefault();

    const inputWeight = document.getElementById("input-weight").value;
    const inputHeight = document.getElementById("input-height").value;

    if (inputWeight === "" || inputHeight === "") {
      // alert("Please enter both weight and height...");
      const inputFields = document.querySelectorAll(".input--container input");
      console.log(inputFields);

      for (const field of inputFields) {
        field.style.outline = "3px solid red";
      }

      return;
    }

    if (isN || inputWeight <=0 || inputWeight > 500){

    }

    setBmi((inputWeight / (inputHeight * inputHeight)).toFixed(1));

    if (bmi < 18.5) {
      setWeight("Under Weight");
    } else if (bmi < 25) {
      setWeight("Normal");
    } else if (bmi < 30) {
      setWeight("Over Weight");
    } else {
      setWeight("Obese");
    }
  }

  return (
    <div className="bmi--container">
      <h1>
        Let's Calculate Your <span className="primary-color">BMI</span>
      </h1>
      <h2>
        Easily determine your body mass index with our accurate calculation
        tool.
      </h2>
      <form action="#">
        <div className="input--container">
          <input
            type="text"
            placeholder="Weight/kg"
            id="input-weight"
            required
          />
          <input
            type="text"
            placeholder="Height/m"
            id="input-height"
            required
          />
        </div>
        <div className="result--container">
          <span>
            {bmi ? (
              <>
                {" "}
                Your BMI is <span className="primary-color">{bmi}</span>
              </>
            ) : (
              ""
            )}
          </span>
          <span>
            {weight ? (
              <>
                {" "}
                Your weight is <span className="primary-color">{weight}</span>
              </>
            ) : (
              ""
            )}
          </span>
        </div>
        <button onClick={bmiCalculate}>Calculate</button>
      </form>
    </div>
  );
}

export default BMICalculator;
