import React from "react";
import "./News.css";
import PrimaryButton from "../PrimaryButton/PrimaryButton";
import { useState, useEffect } from "react";

function News({ margin = "10vh 0 0 0" , count = 3}) {
  
  const [news, setNews] = useState([])


  useEffect(() => {
    
    let newsArray =[];

    try{
    fetch('/news?num=3').then(data => data.json()).then(data => {
      setNews(() => {
        return (newsArray.map((data => {
          <div className="news">
          <div className="news--date">22.03.2023</div>
          <h1>{data.headline}</h1>
          <h2>{data.content}</h2>
          <PrimaryButton value="Read More" isBlack={true} />
        </div>
        } )))
      })
      console.log(data)
    })}
    catch(e){
      console.log(e)
    }
  }, []);


  return (
    <>
      <div className="news--heading" style={{ margin: margin }}>
        <span className="main--heading">LATEST NEWS</span>
      </div>
      <div className="news--container">
        <div className="news">
          <div className="news--date">22.03.2023</div>
          <h1>Yoga For Everyone in 2023</h1>
          <h2>
            This is program designed to make the practice of yoga beneficial for
            people of all ages, abilities, and backgrounds.
          </h2>
          <PrimaryButton value="Read More" isBlack={true} />
        </div>
        <div className="news">
          <div className="news--date">22.03.2023</div>
          <h1>Yoga For Everyone in 2023</h1>
          <h2>
            This is program designed to make the practice of yoga beneficial for
            people of all ages, abilities, and backgrounds.
          </h2>
          <PrimaryButton value="Read More" isBlack={true} />
        </div>
        <div className="news">
          <div className="news--date">22.03.2023</div>
          <h1>Yoga For Everyone in 2023</h1>
          <h2>
            This is program designed to make the practice of yoga beneficial for
            people of all ages, abilities, and backgrounds.
          </h2>
          <PrimaryButton value="Read More" isBlack={true} />
        </div>
        {/* {news} */}


      </div>
    </>
  );
}

export default News;
