import React from "react";
import "./News.css";
import PrimaryButton from "../PrimaryButton/PrimaryButton";
import { useState, useEffect } from "react";

function News({ margin = "10vh 0 0 0", count=-1}) {
  const [news, setNews] = useState([]);
  const [pagination, setPagination] = useState()

  useEffect(() => {

    try{
      fetch('/count-news')
        .then(data => data.json())
        .then((data) => {

          const pageCount = data.pageCount
          let paginationHTML = "";
          for(let i =1; i<= pageCount; i++){
            paginationHTML += `${i}`
          }
          setPagination(paginationHTML)
          console.log(pagination)

        })
    }
    catch(e){
      console.log(e)
    }
    finally{
      
    }

  }, [])

  useEffect(()=> {

    try {
      fetch(count?`/news?num=${count}&page=1`: '/news')
        .then((data) => data.json())
        .then((data) => {
          setNews(data);
        });
    } catch (e) {
      console.log(e);
    }
  }, [count]);

  return (
    <>
      <div className="news--heading" style={{ margin: margin }}>
        <span className="main--heading">LATEST NEWS</span>
      </div>
      <div className="news--container">
        {news.map((item, index) => {
          return (
            <div className="news" key={index}>
              <div className="news--date">{item.date}</div>
              <h1>{item.headline}</h1>
              <h2>{item.content}</h2>
              <PrimaryButton value="Read More" isBlack={true} href="/blog"/>
            </div>
          );
        })}
      </div>
    </>
  );
}

export default News;
